INSERT INTO `tb_jenis_kulit` (`id_jenis_kulit`, `nama`, `deskripsi`, `gambar`) VALUES
(1, 'Normal', 'Kulit normal merupakan kulit yang memiliki kadar minyak wajah dan kadar air pada wajah yang cukup. Kulit normal memiliki pori-pori kecil disepanjang T-Zone (dahi, hidung, dan dagu) sehingga orang yang memiliki jenis kulit wajah ini cenderung jarang mengalami permasalahan terhadap kulit wajah dan jerawat. Perawatan untuk jenis kulit wajah ini biasanya hanya berfokus pada kebersihan wajah, menjaha kulit tetap terhidrasi dan perlindungan terhadap paparan sinar matahari. Ciri-ciri kulit normal adalah kulit lembut, lembab, segar dan bercahaya, halus, serta tidak terlihat minyak yang berlebihan juga tidak terlihat kering.', 'gambar_20250809_143307_3c181d.jpeg'),
(2, 'Kering', 'Kulit kering merupakan kulit yang memiliki sedikit kandungan air sehingga kulit terasa kencang seperti ditarik, gatal, dan dibeberapa bagian tertentu terdapat kulit yang mengelupas. Orang yang memiliki jenis kulit kering memiliki pori-pori yang kecil dan jarang timbul jerawat, namun rentan terdapat garis-garis halus dan kerutan wajah sehingga berpotensi mengalami penuaan dini. Perawatan untuk jenis kulit wajah ini adalah dianjurkan untuk menggunakan produk wajah yang berbahan dasar minyak seperti emolien untuk skin barrier dan produk yang mengandung humektan untuk menjada kulit tetap terhidrasi dan lebih sehat. Ciri dari kulit kering adalah kulit terasa kaku seperti tertarik setelah mencuci muka dan akan mereda setelah dilapisi dengan krim pelembab.', 'gambar_20250809_143323_e7545e.jpeg'),
(3, 'Berminyak', 'Kulit berminyak Merupakan kulit yang mempunyai kadar minyak di permukaan kulit yang berlebihan. Keuntungan dari jenis kulit berminyak adalah orang yang memiliki jenis kulit ini cenderung awet muda, namun kulit wajah yang berminyak rentan mengalami timbulnya jerawat dan komedo karena orang yang memiliki kulit berminyak biasanya memiliki pori-pori wajah yang besar. Perawatan untuk jenis kulit wajah ini adalah selalu menjaga kebersihan kulit wajah untuk menghindari penyumbatan pori-pori wajah oleh sebum.', 'gambar_20250809_143337_d38d40.jpeg'),
(4, 'Kombinasi', 'Kulit kombinasi terjadi jika kadar minyak di wajah tidak merata. Pada area T-Zone (dahi, hidung, dan dagu) memiliki kadar minyak berlebih, sedangkan daerah wajah lainnya memiliki kadar air yang kurang (kering). Karena itu perawatan kulit kombinasi memerlukan perhatian khusus. Perawatan untuk jenis wajah kombinasi yang paling utama adalah jangan takut untuk menggunakan perawatan yang ditargetkan untuk area berbeda dari wajah. Produk yang mengandung BHA cocok digunakan pada kulit berminyak di area T-Zone sebagai exfoliator, sedangkan untuk bagian wajah yang cenderung kering menggunakan pelembab yang berjenis gel dan berminyak sangat dianjurkan untuk menjaga kulit wajah tetap lembab.', 'gambar_20250809_143355_f68960.jpeg'),
(5, 'Sensitif', 'Kulit sensitif adalah jenis kulit yang mudah mengalami iritasi seperti kemerahan, gatal, perih, atau terasa panas akibat paparan bahan kimia tertentu, perubahan cuaca, polusi, maupun produk skincare yang tidak cocok. Kondisi ini umumnya disebabkan oleh lapisan pelindung kulit (skin barrier) yang lemah, sehingga kulit rentan terhadap gangguan dari luar. Pemilik kulit sensitif disarankan untuk menggunakan produk yang bersifat lembut, bebas alkohol, pewangi, serta mengandung bahan yang menenangkan seperti aloe vera atau centella asiatica guna menjaga kulit tetap stabil dan terhindar dari reaksi negatif.', 'gambar_20250809_143413_68e3d6.jpeg');
